mapper.select:
select a.*, u.campus as user_campus from article as a left outer join user as u ON a.user_id = u.user_id where 1 = 1
and ${key} like CONCAT('%', #{word}, '%')
and article_type = #{articleType}
order by article_id desc limit #{startArticleId}, #{articleInterval}

select count(*) from article where 1 = 1
and ${key} like CONCAT('%', #{word}, '%')
and article_type = #{articleType}

mapper.update:
update	article
set		hit = hit + 1
where	article_id = #{articleId}

update 	article
set		subject = #{subject}
		, content = #{content}
where	article_Id = #{articleId}
