mapper.select:
select 	*
from	member
where	userid = #{userid}
and		username = #{username}
and		email = #{email}

mapper.update:
update	member 
set 	username = #{username},
		userpwd = #{userpwd},
        email = #{email},
        address = #{address}
where	userid = #{userid}

mapper.insert:
		insert 
		into member 
		values(	#{userid}
				, #{username}
                , #{userpwd}
                , #{email}
                , #{address}
                , now()
                , false);
